import React, { useContext, useEffect, useState } from 'react';
import { Doughnut } from 'react-chartjs-2';
import Chart from 'react-apexcharts';
import AuthContext from '../AuthContext';

const Dashboard = () => {
  const [saleAmount, setSaleAmount] = useState('');
  const [purchaseAmount, setPurchaseAmount] = useState('');
  const [stores, setStores] = useState([]);
  const [products, setProducts] = useState([]);
  const [monthlySales, setMonthlySales] = useState([]);

  const authContext = useContext(AuthContext);

  useEffect(() => {
    fetchTotalSaleAmount();
    fetchTotalPurchaseAmount();
    fetchStoresData();
    fetchProductsData();
    fetchMonthlySalesData();
  }, []); // Dependency array left empty to run only on component mount

  const fetchTotalSaleAmount = async () => {
    try {
      const response = await fetch(`http://localhost:4012/api/sales/get/${authContext.user}/totalsaleamount`);
      const data = await response.json();
      setSaleAmount(data.totalSaleAmount);
    } catch (error) {
      console.error('Failed to fetch total sale amount:', error);
    }
  };

  const fetchTotalPurchaseAmount = async () => {
    try {
      const response = await fetch(`http://localhost:4012/api/purchase/get/${authContext.user}/totalpurchaseamount`);
      const data = await response.json();
      setPurchaseAmount(data.totalPurchaseAmount);
    } catch (error) {
      console.error('Failed to fetch total purchase amount:', error);
    }
